          # Save the data to the database
     def save(self):
         
        
        self.data["check_out"].set("-- --")
        # check if any fields are empty
        for val in self.data.values():
            if val.get() == "":
                messagebox.showinfo("Error", "Please fill in all the fields")
                return

        # Save the room
        result = db_controller.add_reservation(
            *[self.data[label].get() for label in ("guest_id", "room_no", "type_room","check_in","check_out")]
        )

        if result:
            messagebox.showinfo("Success", "reservation added successfully")
            self.parent.navigate("view")
            self.parent.windows.get("view").handle_refresh()
            # clear all fields
            for label in self.data.keys():
                self.data[label].set('')
        else:
            messagebox.showerror(
                "Error", "Unable to add reservation. Please make sure the data is validated"
            )

